import React, { Component } from "react";
import Select from "react-select";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import DatePicker from "react-datepicker";
import Cookies from "universal-cookie";
import Switch from "react-switch";
import NavBar from "../navBar";
import "react-datepicker/dist/react-datepicker.css";
import "./postNotice.css";
import config from "../../config/config";
const wrapper = {
  display: "flex",
  "flex-direction": "row"
};

const noticeType = [
  {
    label: "Notice",
    value: "notice"
  },
  {
    label: "Application",
    value: "application"
  }
  // {
  //   label: "Schedule", value: 'schedule'
  // },
];

const Urgent = [
  {
    label: "True",
    value: true
  },
  {
    label: "False",
    value: false
  }
];

const department = [
  {
    label: "RTE",
    value: "RTE"
  },
  {
    label: "Finance",
    value: "Finance"
  },
  {
    label: "Student Department",
    value: "Student Department"
  },
  {
    label: "Academic",
    value: "Academic"
  },
  {
    label: "IT",
    value: "IT"
  },
  {
    label: "Other",
    value: "Other"
  }
];

class NoticePost extends Component {
  constructor() {
    super();
    this.state = {
      showError: false,
      title: "",
      titleError: "Title is required",
      subject: "",
      subjectError: "Subject is required",
      noticeType: "",
      noticeTypeError: "Please select notice type",
      urgent: false,
      startDate: new Date(),
      expiryDate: new Date(),
      message: "",
      messageError: "Message is required",
      department: "",
      departmentError: "Please select department",
      modal: false,
      status: true,
      notice: true,
      application: false
    };
    // this.handleChange = this.handleChange.bind(this)
    this.handleStartDate = this.handleStartDate.bind(this);
    this.handleExpiryDate = this.handleExpiryDate.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.showModal = this.showModal.bind(this);
  }
  handleChange = (key, value) => {
    console.log(key, value);
    this.setState({
      [key]: value.target.value
    });
  };

  handleStartDate(date) {
    this.setState({
      startDate: date
    });
  }

  handleExpiryDate(date) {
    this.setState({
      expiryDate: date
    });
  }

  handleNoticeType = selectedOption => {
    console.log("notice type", selectedOption);
    this.setState({
      noticeType: selectedOption.value
    });
  };

  handleDepartment = selectedOption => {
    console.log("notice type", selectedOption);
    this.setState({
      department: selectedOption.value
    });
  };
  handleSubject = text => {
    this.setState({
      subject: text.target.value
    });
  };

  handleUrgent = e => {
    this.setState({
      urgent: !this.state.urgent
    });
  };

  handleMessage = e => {
    this.setState({
      message: e.target.value
    });
    console.log("message", e.target.value);
  };
  postNotice = () => {
    this.props.history.push("/notice");
  };
  browseNotice = () => {
    this.props.history.push("/noticeboard");
  };

  handletitle = text => {
    this.setState({
      title: text.target.value
    });
  };

  handleStatus = e => {
    this.setState({
      status: !this.state.status
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    if (
      this.state.title == "" ||
      this.state.department == "" ||
      this.state.message == ""
    ) {
      this.setState({
        showError: true
      });
      return;
    } else {
      this.submitForm();
    }
  };

  submitForm = async () => {
    // console.log(" i am here");
    const cookies = new Cookies();
    const token = cookies.get("token");

    try {
      console.log("login called inside try");
      const response = await fetch(config.postNotice, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          Authorization: "Bearer " + token
        },
        body: JSON.stringify({
          title: this.state.title,
          schedule: this.state.startDate,
          expiry: this.state.expiryDate,
          subject: this.state.subject,
          content: this.state.message,
          department: this.state.department,
          urgent: this.state.urgent,
          status: this.state.status,
          type: this.state.notice ? "notice" : "application"
        })
      });

      let res = await response.json();

      console.log(res);
      if (res.status) {
        this.setState({
          modal: !this.state.modal
        });
      }
    } catch (e) {}
  };
  showModal() {
    this.setState({
      modal: !this.state.modal
    });
    this.props.history.push("/mynotice");
  }
  handleNotice = key => {
    console.log(key);
    switch (key) {
      case "notice":
        this.setState({
          notice: true,
          application: false
        });
        return;
      case "application":
        this.setState({
          notice: false,
          application: true
        });
        return;
    }
  };

  render() {
    return (
      <div>
        <NavBar />
        <div class="post-notice-wrapper">
          <div class="post-notice">
            <div class="cards">
              <form class="form-notice" onSubmit={this.handleSubmit}>
                <div class="form-group">
                  <h4>Title</h4>
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Enter the title of the notice"
                    required="required"
                    value={this.state.title}
                    onChange={text => {
                      this.handletitle(text);
                    }}
                    maxLength="70"
                  />
                  <text style={{ color: "red" }}>
                    {this.state.showError &&
                      this.state.title == "" &&
                      this.state.titleError}
                  </text>
                </div>
                <div class="form-group">
                  <h4>Subject</h4>
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Enter the subject of your notice"
                    required="required"
                    value={this.state.subject}
                    onChange={text => {
                      this.handleSubject(text);
                    }}
                    maxLength="70"
                  />
                  <text style={{ color: "red" }}>
                    {this.state.showError &&
                      this.state.subject == "" &&
                      this.state.subjectError}
                  </text>
                </div>
                <div class="form-group">
                  <h4>Notice type</h4>
                  {/* <Select options={noticeType} onChange={this.handleNoticeType} />
                  <text style={{ color: 'red' }}>{this.state.showError && this.state.noticeType == "" && this.state.noticeTypeError}</text> */}
                  <div class="row">
                    <div style={{ justifyContent: "center", marginRight: 20 }}>
                      <input
                        style={{ marginRight: 10, marginLeft: 20 }}
                        type="radio"
                        value="notice"
                        checked={this.state.notice}
                        onClick={() => this.handleNotice("notice")}
                      />
                      Notice
                      <br />
                    </div>
                    <div style={{ justifyContent: "center" }}>
                      <input
                        style={{ marginRight: 10, marginLeft: 20 }}
                        type="radio"
                        value="application"
                        checked={this.state.application}
                        onClick={() => this.handleNotice("application")}
                      />
                      Application
                      <br />
                    </div>
                  </div>
                </div>
                <div class="form-group">
                  <div class="row" style={{ marginLeft: 5 }}>
                    <h4>Urgent</h4>
                    {/* <Select options={Urgent} onChange={this.handleUrgent} /> */}
                    <div style={{ marginLeft: 20 }}>
                      <Switch
                        onChange={this.handleUrgent}
                        checked={this.state.urgent}
                        id="normal-switch"
                      />
                    </div>
                  </div>
                </div>
                <div class="form-group">
                  <div class="row" style={{ marginLeft: 5 }}>
                    <h4>Status</h4>
                    {/* <Select options={Urgent} onChange={this.handleStatus} /> */}
                    <div style={{ marginLeft: 20 }}>
                      <Switch
                        onChange={this.handleStatus}
                        checked={this.state.status}
                        id="normal-switch"
                      />
                    </div>
                  </div>
                </div>
                <div class="form-group">
                  <h4>Department</h4>
                  <Select
                    options={department}
                    onChange={this.handleDepartment}
                  />
                  <text style={{ color: "red" }}>
                    {this.state.showError &&
                      this.state.department == "" &&
                      this.state.departmentError}
                  </text>
                </div>
                <div class="form-group">
                  <h4>Schedule Date</h4>
                  <DatePicker
                    className="date-picker"
                    selected={this.state.startDate}
                    onChange={this.handleStartDate}
                  />
                </div>

                <div class="form-group">
                  <h4>Expire date</h4>
                  <DatePicker
                    className="date-picker"
                    selected={this.state.expiryDate}
                    onChange={this.handleExpiryDate}
                  />
                </div>

                <div class="form-group">
                  <h4>Message</h4>
                  <textarea
                    class="form-control rounded-0"
                    rows="6"
                    onChange={this.handleMessage}
                    maxLength="300"
                  />
                  <text style={{ color: "red" }}>
                    {this.state.showError &&
                      this.state.message == "" &&
                      this.state.messageError}
                  </text>
                </div>

                <input
                  type="submit"
                  class="btn btn-primary btn-lg btn-block"
                  onClick={this.handleSubmit}
                  value="Submit"
                />
              </form>
            </div>
          </div>
        </div>
        <Modal isOpen={this.state.modal} toggle={this.showModal}>
          <ModalHeader toggle={this.showModal}>Successful</ModalHeader>
          <ModalBody>your notice has been posted.</ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.showModal}>
              Ok
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default NoticePost;
